def fibonacci(n):
    # Initialize the memoization table
    memo = {}

    # Define the recursive function to calculate Fibonacci numbers
    def fib(n):
        # If n is 0 or 1, the Fibonacci number is n
        if n in (0, 1):
            return n

        # If we have calculated the Fibonacci number before, return it
        if n in memo:
            return memo[n]

        # Calculate the Fibonacci number and store it in the memoization table
        memo[n] = fib(n-1) + fib(n-2)

        # Return the calculated Fibonacci number
        return memo[n]

    # Calculate the nth Fibonacci number using the recursive function
    return fib(n)

