function findMedianSortedArrays(nums1, nums2) {
  // Combine the two sorted arrays into one
  let nums = nums1.concat(nums2);

  // If the combined array has an odd number of elements, the median
  // is the middle element
  if (nums.length % 2 === 1) {
    return nums[nums.length / 2];
  }

  // If the combined array has an even number of elements, the median
  // is the average of the two middle elements
  else {
    let mid = Math.floor(nums.length / 2);
    return (nums[mid] + nums[mid - 1]) / 2;
  }
}

