import os
from collections import Counter

def get_top_words(file_path):
    words = []
    with open(file_path, 'r') as f:
        for line in f:
            words.extend(line.strip().split())
    return sorted(Counter(words).most_common(5), key=lambda x: x[1], reverse=True)

def get_top_words_from_dir(dir_path):
    top_words = []
    for filename in os.listdir(dir_path):
        if filename.endswith('.txt'):
            top_words.extend(get_top_words(os.path.join(dir_path, filename)))
    return top_words

if __name__ == '__main__':
    top_words = get_top_words_from_dir('path/to/directory')
    for word in top_words:
        print(word[0], word[1])

